	@ActionTypeName
	)

-- Get the identity value
SET @ActionTypeId = SCOPE_IDENTITY()

BEGIN
-- Create a temp table to store the select results CREATE TABLE #PageIndex ( [IndexId] int IDENTITY (1, 1) NOT NULL, [ActionTypeID] int )
-- Insert into the temp table INSERT INTO #PageIndex ( [ActionTypeID] ) SELECT TOP {3} [ActionTypeID] FROM [dbo].[tblActionType] {0} ORDER BY {1}
-- Return paged results SELECT O.[ActionTypeID], O.[ActionTypeName] FROM [dbo].[tblActionType] O, #PageIndex PageIndex WHERE PageIndex.IndexId > {2} AND O.[ActionTypeID] = PageIndex.[ActionTypeID] ORDER BY PageIndex.IndexId
-- get total count SELECT COUNT(*) AS TotalRowCount FROM [dbo].[tblActionType] {0};
